import {
  SET_USER, 
  SET_AUTHENTICATED,
  SET_UNAUTHENTICATED, 
  LOADING_USER,
  LIKE_MAIL
} from '../types'

const initialState = {
	authenticated: false,
	loading: false,
	credentials: {},
	likes:[],
	notifications: []
}

export default function(state = initialState, action){
	switch(action.type){
		case SET_AUTHENTICATED:
			return {
				...state,
				authenticated: true
			};
		case SET_UNAUTHENTICATED:
			return initialState;
		case SET_USER:
		return {
			authenticated: true,
			loading: false,
			...action.payload
		}
		case LOADING_USER:
			return {
				...state,
				loading:true
			}
		case LIKE_MAIL:
		return {
			...state,
			likes: [
			...state.likes,
			{
				userHandle:state.credentials.handle,
				mailId: action.payload.mailId
		    }

		  ]
		}
		case UNLIKE_MAIL:
		 return {
		 	...state,
		 	likes: state.likes.filter(like => like.mailId === action.payload.mailId)
		 }
		default:
			return state
	}
}
