import React, {Component, Fragment} from 'react';
import PropTypes from 'prop-types';
import withStyles from '@material-ui/core/styles/withStyles';
import MyButton from '../util/MyButton';
import dayjs from 'dayjs';
import { Link } from 'react-router-dom';

//MUI STUFF
import Dialog from '@material-ui/core/Dialog';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import CircularProgress from '@material-ui/core/CircularProgress';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
//Icons
import CloseIcon from '@material-ui/icons/Close';
import UnfoldMore from '@material-ui/icons/UnfoldMore';

//Redux
import { connect } from 'react-redux';
import { getMail } from '../redux/actions/dataactions';

const styles = (theme)=> ({
	...theme.spread1	
})

class MailDialog extends Component {
	state = {
		open: false
	}
	handleOpen = ()=> {
		this.setState({open:true})
		this.props.getMail(this.props.mailId)
	}
	handleClose = () => {
		this.setState({ open: false})
	}

	render() {

		const {
			classes, 
			mail: { 
			  mailId, 
			  body, 
			  createdAt, 
			  likeCount, 
			  commentCount, 
			  userImage, 
	     	  userHandle
		},
		UI: {loading}
	 } = this.props;

	 const dialogMarkup = loading ? (
	 	<div className={classes.spinnerDiv}>
	 	<CircularProgress size={200} thickness={2}/>
	 	</div>
	 	) : (
	 	<Grid container spacing={16}>
	 	 <Grid item sm={5}>
	 	  <img src={userImage} alt='profile' className={classes.profileImage} />
	 	 </Grid>
	 	 <Grid item sm={5}>
	 	  <Typography
	 	    component={Link}
	 	    color='primary'
	 	    variant='h5'
	 	    to={`/users/${userHandle}`}
	 	     >
	 	 	@{userHandle}
	 	 	</Typography>
	 	 	<hr className={classes.invisibleSeparator} />
	 	 	<Typography variant='body2' color='textSecondary'>
	 	 	 {dayjs(createdAt).format('h:mm a, MMM DD YYYY')}
	 	 	</Typography>
	 	 	<hr className={classes.invisibleSeparator}/>
	 	 	<Typography variant='body1'>
	 	 	 {body}
	 	 	</Typography>
	 	 </Grid>
	 	</Grid>
	 	)
	 return (
		<Fragment>
			<MyButton onClick={this.handleOpen} tip='Expand mail' tipClassName={classes.expandButton}>
			 <UnfoldMore color='primary' />
			</MyButton>
			<Dialog
		    	open={this.state.open}
		        onClose={this.handleClose} 
		        fullWidth 
		        maxWidth='sm'
		        >
			  	<MyButton 
			  	tip='close' 
			  	onClick={this.handleClose} 
			  	tipClassName={classes.closeButton}
			  	>
			  	<CloseIcon />
			  	</MyButton>
			 <DialogContent className={classes.DialogContent}>
			  {dialogMarkup}
			 </DialogContent>
			</Dialog>
		</Fragment>
	)
	}
}
MailDialog.propTypes = {
	getMail: PropTypes.func.isRequired,
	mailId: PropTypes.string.isRequired,
	userHandle: PropTypes.string.isRequired,
	mail: PropTypes.object.isRequired,
    UI: PropTypes.object.isRequired
}

const mapStateToprops = state => ({
	mail:state.data.mail,
	UI: state.UI
})

const mapActionToProps = {
	getMail
}
export default connect(mapStateToprops,mapActionToProps)(withStyles(styles)(MailDialog));
