import { SET_ERRORS, CLEAR_ERRORS, LOADING_UI, STOP_LOADING_UI } from '../types'

const initiateState = {
	loading: false,
	errors: null
}

export default function(state=initiateState, action){
	switch(action.type){
		case SET_ERRORS:
			return {
				...state,
				loading:false,
				errors:action.payload
			};
		case CLEAR_ERRORS:
		 return {
		 	...state,
		 	loading:false,
		 	errors:null
		 };
		 case LOADING_UI:
		 	return {
		 		...state,
		 		loading: true
		 	};
		case STOP_LOADING_UI:
			return {
				...state,
				loading: false
			}

		 default:
		 return state;
	}
}
