import React, { Component } from 'react';
import withStyles from '@material-ui/styles/withStyles';
import Link from 'react-router-dom/Link';
import Typography from '@material-ui/core/Typography';
//
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';

const styles ={
	card: {
		display: 'flex',
		marginBottom: 20,

	},
	image:{
		minWidth:200,
	},
	content:{
		padding: 25,
		objectFit: 'cover'
	}
}

class Mail extends Component {
	render() {
	const {classes, mail : {imageUrl, userHandle, body, createdAt, mailId, likeCount, commentCount} } = this.props;
	// const {body,createdAt, userImage, userHandle, mailId, likeCount, commentCount} = this.props;
		return(
			<Card className={classes.card}>
			  <CardMedia
			  image={imageUrl} 
			  title="profile image" 
			  className={classes.image}
			  />
			    <CardContent className={classes.content}>
			    <Typography 
			     variant="h5" 
			     component={Link} 
			     to={`/users/${userHandle}`}
			     color='primary'
			     >
			     {userHandle}
			     </Typography>
			   <Typography 
			   variant="body2" color="textSecondary">
			   {createdAt}
			   </Typography>
			   <Typography variant="body1">{body}</Typography>
			  </CardContent>  
			</Card>
		)
	}
}

export default withStyles(styles)(Mail);
