import React, {Component, Fragment} from 'react';
import PropTypes from 'prop-types';
import withStyles from '@material-ui/core/styles/withStyles';
import MyButton from '../util/MyButton';

//mui
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import CircularProgress from '@material-ui/core/CircularProgress';
import AddIcon from '@material-ui/icons/Add';
import CloseIcon from '@material-ui/icons/Close';


//redux
import { connect } from 'react-redux';
import { postMail } from '../redux/actions/dataactions';

const styles = theme => ({

	submitButton: {
		position: 'relative'
	},
	progressSpinner: {
		position: 'absolute'
	},
	closeButton: {
		position: 'absolute',
		left: '90%',
		top: '10%'
	}
})
class PostMail extends Component {
	state= {
		open: false,
		body:'',
		errors: {}
	}
	componentWillReceiveProps(nextProps){
		if (nextProps.UI.errors) {
			this.setState({
				errors: nextProps.UI.errors
			})
		}
	}
	handleOpen = () => {
		this.setState({open: true})
	}
	handleClose= () => {
		this.setState({open: false})
	}
	handleChange=(event) => {
		this.setState({[event.target.name] : event.target.value})
	}
	handleSubmit =(event) => {
		event.preventDefault();
		this.props.postMail({ body: this.state.body})
	}

	render(){
		const { errors } = this.state;
		const { classes, UI:{ loading}} = this.props

		return (
			<Fragment> 
			 <MyButton onClick={this.handleOpen } tip='Post Mail' >
			   <AddIcon />
			  </MyButton>
			  <Dialog
			  open={this.state.open} onClose={this.handleClose} fullWidth maxWidth='sm'>
			  	<MyButton tip='close' onClick={this.handleClose} tipClassName={classes.closeButton}>
			  		<CloseIcon />
			  	</MyButton>
			  	<DialogTitle>Post a new Mail</DialogTitle>
			  	<DialogContent>
			  		<form onSubmit={this.handleSubmit}>
			  		  <TextField 
			  		    name='body'
			  		    type='text' 
			  		    label='MAIL'
			  		    multiline 
			  		    rows='3' 
			  		    placeholder='whats your destination?'
			  		    error={errors.body ? true : false}
			  		   helperText={errors.body}
			  		  className={classes.textField}
			  		  onChange={this.handleChange}
			  		  fullWidth
			  		   />
			  		  <Button type='submit' variant='contained' color='primary'
			  		  className={classes.submitButton} disabled={loading}>
			  		    Submit
			  		    { loading && (
			  		     <CircularProgress size={30} className={classes.progressSpinner} />
			  		    )}
 			  		  </Button>
			  		</form>
			  	</DialogContent>
			  </Dialog>
			</Fragment>
		)
	}
}
PostMail.propTypes = {
	postMail: PropTypes.func.isRequired,
	UI: PropTypes.object.isRequired
}
const mapStateToProps = (state) => ({
	UI: state.UI
})

export default connect(mapStateToProps, { postMail })(withStyles(styles)(PostMail))
