import React, {Component} from 'react';
import Grid from '@material-ui/core/Grid';
import PropTypes from 'prop-types';
import axios from 'axios';
import Mail from '../components/Mail';
import Profile from '../components/profile';
import { connect } from 'react-redux';
import { getMail } from '../redux/actions/dataActions';



class home extends Component {

	componentDidMount(){
		this.props.getMails
		}

	render() {
		const { mails,loading } = this.props.data;
		let recentMailsMarkup = loading?(
			mails.map((mail) => 
				<Mail key={mail.mailId} mail={mail} />)

			): (<p>Loading...</p>
			)

		return (
		 <Grid container spacing={10}>
		   <Grid item sm={8} xs={12}>
		   		{recentMailsMarkup}
		   </Grid>
		   <Grid item sm={4} xs={12}>
		    <Profile />
		   </Grid>
		 </Grid>

		);
	}
}
home.propTypes = {
getMails: PropTypes.func.isRequired,
data: PropTypes.object.isRequired
}

const mapStateToProps = state => ({
	data: state.data
})

export default connect(mapStateToProps, { getMails })(home);
